import { Injectable } from "@angular/core";
import { HTMLLegendPlugin } from "../types/chart";
import { BehaviorSubject } from "rxjs";
import { PatternService } from "./pattern.service";
import { FormatUtilitiesService } from "./format-utilities.service";
import { ChartOptions } from "chart.js";

export const LEGEND_FONT_SIZE = 14;
export const LEGEND_LABEL_LEFT_MARGIN = "6px";
export const LEGEND_BOX_SIZE = "22px";
export const LEGEND_BOX_POINT_SIZE = 6;
export const LEGEND_BOX_BORDER = "2px";

export interface Chart {
  update(): void;
  toggleDataVisibility(datasetIndex: number): void;
  isDatasetVisible(datasetIndex: number): boolean;
  getDataVisibility(index: number): boolean;
  setDatasetVisibility(datasetIndex: number, visible: boolean): void;
  plugins: HTMLLegendPlugin;
  options:
    | ChartOptions<"radar">
    | ChartOptions<"doughnut">
    | ChartOptions<"bar">
    | ChartOptions<"line">;
  config: {
    type?: string;
    data: { labels: string[]; datasets: unknown[]; data?: unknown[] };
  };
}

export interface ChartItem {
  fontColor: string;
  hidden: boolean;
  text: string;
  fillStyle: string;
  strokeStyle: string;
  lineWidth: number;
  datasetIndex: number;
  index: number;
  lineCap?: string;
}

@Injectable({
  providedIn: "root",
})
export class ChartLegendService {
  constructor(
    private readonly patternService: PatternService,
    private readonly formatUtilitiesService: FormatUtilitiesService
  ) {}

  public getHtmlLegendPlugin(
    legendContainer: BehaviorSubject<HTMLElement | null>,
    selectMode: BehaviorSubject<boolean>,
    onHoverIndex: BehaviorSubject<any>,
    disableAccessibility: boolean,
    patternsColors: string[],
    patternsList: ((
      hover: boolean,
      color: string,
      disableAccessibility: boolean
    ) => CanvasPattern)[],
    enableHoverFeature: boolean,
    maxValueToDisplay?: number,
    chartData?: any
  ): HTMLLegendPlugin {
    const chartLegendService = this;
    return {
      id: "htmlLegend",
      afterUpdate(chart: any) {
        const isDoughnut: boolean = chart.config.type === "doughnut";
        const flexDirection = isDoughnut ? "column" : "row";
        const ul: HTMLUListElement = chartLegendService.getOrCreateLegendList(
          legendContainer,
          flexDirection
        );
        ul.style.margin = "1.375rem 1.0625rem";
        while (ul.firstChild) {
          ul.firstChild.remove();
        }
        const items: ChartItem[] =
          chart.options.plugins.legend.labels.generateLabels(chart);
        items.forEach((item: ChartItem): void => {
          const isDoughnut: boolean = chart.config.type === "doughnut";
          const index: number = isDoughnut ? item.index : item.datasetIndex;
          const li: HTMLElement =
            chartLegendService.createHtmlLegendListElement(
              chart,
              selectMode,
              index
            );
          if (isDoughnut) {
            const isOthersElement: boolean = index + 1 === maxValueToDisplay;
            li.style.marginTop = "12px";
            if (isOthersElement) {
              li.style.position = "relative";
            }
          } else {
            li.style.marginRight = "10px";
          }
          li.style.width = "max-content";
          li.style.cursor = "pointer";
          let liContent: HTMLElement;
          if (!selectMode.value) {
            liContent = chartLegendService.createLegendElementWithPatterns(
              item,
              chart,
              onHoverIndex,
              disableAccessibility,
              patternsColors,
              patternsList,
              enableHoverFeature
            );
          } else {
            liContent = chartLegendService.createLegendElementWithCheckbox(
              chart,
              item,
              selectMode,
              onHoverIndex,
              patternsColors,
              enableHoverFeature
            );
          }
          liContent.style.boxSizing = "border-box";
          li.appendChild(liContent);
          li.appendChild(chartLegendService.createHtmlLegendItemText(item));
          if (
            isDoughnut &&
            maxValueToDisplay &&
            chartLegendService.hasOthersTooltipToDisplay(
              chartData,
              maxValueToDisplay,
              index
            )
          ) {
            li.appendChild(
              chartLegendService.createTooltipAndItsIcon(
                chartData,
                maxValueToDisplay
              )
            );
          }
          ul.appendChild(li);
        });
      },
    };
  }

  public hasOthersTooltipToDisplay(
    doughnutData: any,
    maxValueToDisplay: number,
    index: number
  ) {
    return (
      doughnutData.data.length > maxValueToDisplay &&
      index === maxValueToDisplay - 1
    );
  }

  public getOrCreateLegendList(
    legendContainer: BehaviorSubject<HTMLElement | null>,
    flexDirection: string
  ): HTMLUListElement {
    let listContainer = legendContainer?.value?.querySelector("ul");
    if (!listContainer) {
      listContainer = document.createElement("ul");
      listContainer.style.display = "flex";
      listContainer.style.flexDirection = flexDirection;
      listContainer.style.margin = "0";
      listContainer.style.padding = "0";
      legendContainer.value?.appendChild(listContainer);
    }
    return listContainer;
  }

  public createHtmlLegendListElement(
    chart: Chart,
    selectMode: BehaviorSubject<boolean>,
    elementIndex: number
  ) {
    const li: HTMLElement = document.createElement("li");
    li.style.alignItems = "center";
    li.style.cursor = selectMode.value ? "" : "pointer";
    li.style.display = "flex";
    li.style.flexDirection = "row";
    li.setAttribute("data-test-id", `legend-item-${elementIndex}`);
    li.onclick = () => {
      if (!selectMode.value) {
        this.hideAllButThis(chart, elementIndex, selectMode);
        chart.update();
      } else {
        this.switchItemVisibility(chart, elementIndex, selectMode);
      }
    };
    return li;
  }

  public hideAllButThis(
    chart: Chart,
    elementIndex: number,
    selectMode: BehaviorSubject<boolean>
  ) {
    if (!selectMode.value) {
      const dataSets: unknown[] = this.getChartsData(chart);
      selectMode.next(true);
      dataSets.forEach((_data, index) => {
        if (index !== elementIndex) {
          this.switchItemVisibility(chart, index);
        }
      });
    }
  }

  public switchItemVisibility(
    chart: Chart,
    elementIndex: number,
    selectMode?: BehaviorSubject<boolean>
  ): void {
    if (this.isMonoDataSetChart(chart)) {
      chart.toggleDataVisibility(elementIndex);
    } else {
      chart.setDatasetVisibility(
        elementIndex,
        !chart.isDatasetVisible(elementIndex)
      );
    }

    if (selectMode && this.allDataVisible(chart)) {
      selectMode.next(false);
    }
    chart.update();
  }

  public createLegendElementWithPatterns(
    item: ChartItem,
    chart: Chart,
    onHoverIndex: BehaviorSubject<any>,
    disableAccessibility: boolean,
    patternsColors: string[],
    patternsList: ((
      hover: boolean,
      color: string,
      disableAccessibility: boolean
    ) => CanvasPattern)[],
    enableHoverFeature: boolean
  ): HTMLElement {
    const isDoughnut: boolean = chart.config.type === "doughnut";
    const index: number = isDoughnut ? item.index : item.datasetIndex;
    const img: HTMLImageElement = new Image();
    const boxSpan: HTMLElement = this.createHtmlLegendLine(
      item,
      chart.config.type
    );
    const pattern: CanvasPattern = patternsList[index](
      false,
      patternsColors[index],
      disableAccessibility
    );
    const patternCanvas: HTMLCanvasElement =
      this.patternService.getPatternCanvas(pattern);
    img.src = patternCanvas.toDataURL();
    boxSpan.style.background = `url(${img.src})`;
    boxSpan.style.backgroundSize = "cover";
    boxSpan.style.borderColor = patternsColors[index];
    boxSpan.style.borderWidth = LEGEND_BOX_BORDER;

    if (enableHoverFeature) {
      boxSpan.onmouseover = (): void => {
        isDoughnut
          ? onHoverIndex.next(index)
          : onHoverIndex.next({
              ...onHoverIndex.value,
              dataSetIndex: index,
            });
      };
      boxSpan.onmouseleave = (): void => {
        onHoverIndex.next(null);
      };
    }
    return boxSpan;
  }

  public createHtmlLegendLine(
    item: ChartItem,
    type: string | undefined
  ): HTMLDivElement {
    const boxSpan = document.createElement("div");
    if (type !== "doughnut") {
      boxSpan.style.background = "rgba(0, 0, 0, 0.1)";
      boxSpan.style.borderColor = item.strokeStyle;
      boxSpan.style.borderWidth = LEGEND_BOX_BORDER;
    }
    boxSpan.style.borderRadius = "5px";
    boxSpan.style.borderStyle = "solid";
    boxSpan.style.display = "flex";
    boxSpan.style.justifyContent = "center";
    boxSpan.style.alignItems = "center";
    boxSpan.style.minWidth = LEGEND_BOX_SIZE;
    boxSpan.style.marginRight = LEGEND_LABEL_LEFT_MARGIN;
    boxSpan.style.minHeight = LEGEND_BOX_SIZE;
    return boxSpan;
  }

  public createLegendElementWithCheckbox(
    chart: Chart,
    item: ChartItem,
    selectMode: BehaviorSubject<boolean>,
    onHoverIndex: BehaviorSubject<any>,
    patternsColors: string[],
    enableHoverFeature: boolean
  ): HTMLElement {
    const isDoughnut: boolean = chart.config.type === "doughnut";
    const index: number = isDoughnut ? item.index : item.datasetIndex;
    const checkbox: HTMLElement = this.createLegendCheckbox(
      chart,
      item,
      patternsColors
    );
    const labels = chart.config.data.labels;
    const allCheckBoxesVisible: boolean = labels.every((_, index: number) =>
      chart.getDataVisibility(index)
    );
    if (allCheckBoxesVisible) {
      if (isDoughnut) {
        selectMode.next(false);
        onHoverIndex.next(-1);
      }
      return checkbox;
    }
    if (enableHoverFeature) {
      checkbox.onmouseover = (): void => {
        isDoughnut
          ? onHoverIndex.next(index)
          : onHoverIndex.next({
              ...onHoverIndex.value,
              dataSetIndex: index,
            });
        chart.update();
      };
      checkbox.onmouseleave = (): void => {
        isDoughnut
          ? onHoverIndex.next(null)
          : onHoverIndex.next({
              ...onHoverIndex.value,
              dataSetIndex: -1,
            });
        chart.update();
      };
    }
    return checkbox;
  }

  public createLegendCheckbox(
    chart: Chart,
    item: ChartItem,
    patternsColors: string[]
  ): any {
    const isDoughnut: boolean = chart.config.type === "doughnut";
    const index: number = isDoughnut ? item.index : item.datasetIndex;
    const checkbox = document.createElement("input");
    checkbox.setAttribute("type", "checkbox");
    checkbox.setAttribute("data-test-id", `legend-checkbox-${index}`);
    const isDataSetVisible = this.isChartDataVisible(chart, index);
    const patternColor = patternsColors ? patternsColors[index] : undefined;
    this.addCheckboxStyle(
      isDataSetVisible,
      item,
      checkbox,
      patternColor as string
    );
    return checkbox;
  }

  public addCheckboxStyle(
    isDataSetVisible: boolean,
    item: ChartItem,
    checkbox: Element,
    patternColor: string
  ): void {
    let backgroundColor = "#fff";
    let borderColor = "#666";
    if (isDataSetVisible) {
      //Default white for patterns chart
      backgroundColor = this.isDefaultWhiteColor(item.strokeStyle)
        ? patternColor
        : item.strokeStyle;
      borderColor = this.isDefaultWhiteColor(item.strokeStyle)
        ? patternColor
        : item.strokeStyle;
      checkbox.setAttribute("checked", "" + isDataSetVisible);
    }
    checkbox.setAttribute("class", "mc-checkbox__input");
    checkbox.setAttribute(
      "style",
      `background-color: ${backgroundColor};
      min-width: ${LEGEND_BOX_SIZE};
      min-height: ${LEGEND_BOX_SIZE};
      margin-right: ${LEGEND_LABEL_LEFT_MARGIN};
      border-color: ${borderColor};`
    );
  }

  public createHtmlLegendItemText(item: ChartItem): HTMLParagraphElement {
    const textContainer = document.createElement("p");
    textContainer.style.color = item.fontColor;
    textContainer.style.fontSize = `${LEGEND_FONT_SIZE}px`;
    textContainer.style.margin = "0";
    textContainer.style.padding = "0";

    const text = document.createTextNode(item.text);
    textContainer.appendChild(text);
    return textContainer;
  }

  public createTooltipAndItsIcon(
    doughnutData: any,
    maxValueToDisplay: number
  ): HTMLDivElement {
    const iconTopWrapper = document.createElement("div");
    const iconWrapper = document.createElement("div");
    const icon = document.createElement("img");
    iconTopWrapper.style.position = "absolute";
    iconTopWrapper.style.right = "-32px";
    icon.src = "src/assets/icons/Navigation_Notification_Question_24px.svg";
    icon.style.top = "0";
    icon.style.width = "1.5rem";
    icon.style.filter =
      "invert(38%) sepia(19%) saturate(18%) hue-rotate(337deg) brightness(97%) contrast(85%)";
    iconWrapper.style.position = "relative";
    iconWrapper.style.display = "flex";
    const tooltip = this.createLegendOthersTooltip(
      doughnutData,
      maxValueToDisplay
    );
    icon.onmouseover = () => {
      (iconWrapper.firstElementChild as HTMLElement).style.visibility =
        "visible";
    };
    icon.onmouseleave = () => {
      (iconWrapper.firstElementChild as HTMLElement).style.visibility =
        "hidden";
    };
    iconTopWrapper.appendChild(iconWrapper);
    iconWrapper.appendChild(tooltip);
    iconWrapper.appendChild(icon);
    return iconTopWrapper;
  }

  private createLegendOthersTooltip(
    doughnutData: any,
    maxValueToDisplay: number
  ): HTMLDivElement {
    const tooltip = document.createElement("div");
    tooltip.style.visibility = "hidden";
    tooltip.style.position = "absolute";
    tooltip.style.zIndex = "10";
    tooltip.style.width = "350px";
    tooltip.style.bottom = "100%";
    tooltip.style.left = "50%";
    tooltip.style.marginLeft = "-150px";
    tooltip.style.background = "#FFFFFF";
    tooltip.style.boxShadow = "0px 1px 5px rgba(0, 0, 0, 0.2)";
    tooltip.style.borderRadius = "0.5rem";
    tooltip.style.fontSize = "14px";
    tooltip.style.overflow = "hidden";
    this.addOthersTooltipLines(doughnutData, maxValueToDisplay, tooltip);
    return tooltip;
  }

  private addOthersTooltipLines(
    doughnutData: any,
    maxValueToDisplay: number,
    tooltip: HTMLDivElement
  ): void {
    const startIndex = maxValueToDisplay - 1;
    doughnutData.data
      .slice(startIndex)
      .forEach((_ignore: any, index: number) => {
        const dataIndex = startIndex + index;
        const textWrapper = document.createElement("div");
        textWrapper.style.display = "flex";
        textWrapper.style.justifyContent = "space-between";
        textWrapper.style.padding = "0.5rem";
        textWrapper.style.border = "1px solid #CCCCCC";
        const label = document.createElement("span");
        label.appendChild(
          document.createTextNode(doughnutData.labels[dataIndex])
        );
        const value = document.createElement("span");
        value.appendChild(
          document.createTextNode(
            this.formatUtilitiesService.formatValueAndRate(
              doughnutData,
              dataIndex
            )
          )
        );
        textWrapper.appendChild(label);
        textWrapper.appendChild(value);
        tooltip.appendChild(textWrapper);
      });
  }

  private getChartsData(chart: any): unknown[] {
    let dataSets: unknown[] = chart.config.data.datasets;
    if (this.isMonoDataSetChart(chart)) {
      dataSets = chart.config.data.datasets[0].data;
    }
    return dataSets;
  }

  private isMonoDataSetChart(chart: Chart): boolean {
    const { type } = chart.config;
    return type === "pie" || type === "doughnut";
  }

  private allDataVisible(chart: Chart): boolean {
    let allVisible = true;
    const chartsData: unknown[] = this.getChartsData(chart);
    chartsData.forEach((_data, dataIndex) => {
      allVisible = allVisible && this.isChartDataVisible(chart, dataIndex);
    });
    return allVisible;
  }

  private isChartDataVisible(chart: Chart, dataIndex: number): boolean {
    if (this.isMonoDataSetChart(chart)) {
      return chart.getDataVisibility(dataIndex);
    } else {
      return chart.isDatasetVisible(dataIndex);
    }
  }

  private isDefaultWhiteColor(color: string): boolean {
    return color === "#00000000";
  }
}

import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class ColorFunctionsService {
  constructor() {}

  /**
   * Adds alpha transparency to a color.
   * @param color The color in hex format (e.g., '#FF5733').
   * @param opacity The opacity value between 0 and 1.
   * @returns The color with the added alpha value as a hex code (e.g., '#FF573380').
   */
  addAlpha(color: string, opacity: number): string {
    const _opacity = Math.round(Math.min(Math.max(opacity || 1, 0), 1) * 255);
    return color + _opacity.toString(16).toUpperCase();
  }
}

import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";
import { Chart, Plugin } from "chart.js";
import { DoughnutData } from "../types/doughnut-data";
import { FormatUtilitiesService } from "./format-utilities.service";
import { ColorFunctionsService } from "./color-function.service";
import { ChartLegendService } from "./chart-legend.service";

@Injectable({
  providedIn: "root",
})
export class DoughnutChartFunctionsService {
  doughnutRef = new BehaviorSubject<any>(null);
  onHoverIndex = new BehaviorSubject<any>(null);
  backgroundColor = new BehaviorSubject<CanvasPattern[] | null>(null);
  centeredLabel = new BehaviorSubject<string | null>(null);

  constructor(
    private readonly formatUtilitiesService: FormatUtilitiesService,
    private readonly colorFunctionsService: ColorFunctionsService,
    private readonly chartLegendService: ChartLegendService
  ) {}

  public privateGetHtmlLegendPlugin(
    legendContainer: BehaviorSubject<HTMLElement | null>,
    selectMode: BehaviorSubject<boolean>,
    disableAccessibility: boolean,
    patternsColors: string[],
    patternsList: Array<
      (
        hover: boolean,
        color: string,
        disableAccessibility: boolean
      ) => CanvasPattern
    >,
    maxValueToDisplay: number,
    doughnutData: any,
    enableHoverFeature: boolean
  ): Plugin<"doughnut"> {
    return this.chartLegendService.getHtmlLegendPlugin(
      legendContainer,
      selectMode,
      this.onHoverIndex,
      disableAccessibility,
      patternsColors,
      patternsList,
      enableHoverFeature,
      maxValueToDisplay,
      doughnutData
    );
  }

  public getBackgroundColor(
    patternsColors: string[],
    patternsList: Array<
      (
        hover: boolean,
        color: string,
        disableAccessibility: boolean
      ) => CanvasPattern
    >,
    disableAccessibility: boolean,
    enableHoverFeature: boolean
  ): CanvasPattern[] {
    if (this.onHoverIndex.value !== null && enableHoverFeature) {
      return patternsList.map((pattern, index) =>
        this.onHoverIndex.value === index
          ? pattern(false, patternsColors[index], disableAccessibility)
          : pattern(true, patternsColors[index], disableAccessibility)
      );
    } else {
      return patternsList.map((pattern, index) =>
        pattern(false, patternsColors[index], disableAccessibility)
      );
    }
  }

  public getBorderColor(
    patternsColors: string[],
    enableHoverFeature: boolean
  ): string[] {
    if (this.onHoverIndex.value !== null && enableHoverFeature) {
      return patternsColors.map((color, index) =>
        this.onHoverIndex.value === index
          ? color
          : this.colorFunctionsService.addAlpha(color, 0.2)
      );
    } else {
      return patternsColors;
    }
  }

  public getOnHoverOptions() {
    return (_ignore: unknown, activeElements: any[]): void => {
      if (activeElements[0] !== undefined) {
        this.onHoverIndex.next(activeElements[0].index);
      } else {
        this.onHoverIndex.next(null);
      }
    };
  }

  public getFormattedText(str: string): string {
    return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
  }

  public getCenteredLabelPlugin(
    doughnutData: DoughnutData[]
  ): Plugin<"doughnut"> {
    return {
      id: "centeredLabelPlugin",
      afterDatasetDraw: (chart: Chart<"doughnut">) => {
        const selectedIndexes: number[] = [];
        const arcElements = chart.getDatasetMeta(0).data;

        arcElements.forEach((arcElement: any, index: number) => {
          if (arcElement.startAngle !== arcElement.endAngle) {
            selectedIndexes.push(index);
          }
        });

        const total = (chart as any)._metasets[0]?._dataset?.raw_value
          ?.filter((_: any, index: number) => selectedIndexes.includes(index))
          .reduce((acc: number, currentValue: any) => acc + currentValue, 0);
        const unit = doughnutData[0].unit ?? "";
        this.centeredLabel.next(
          `${this.formatUtilitiesService.formatWithThousandsSeparators(
            total
          )}${unit}`
        );
      },
    };
  }

  public getDoughnutLabels(
    labels: string[],
    data: DoughnutData[],
    maxValues: number,
    othersLabel: string
  ): string[] {
    let truncatedLabels = labels.slice(0);
    let truncatedData = data.slice(0);

    if (labels.length > maxValues) {
      truncatedData = this.groupDataAfterNthValue(data, maxValues);
      truncatedLabels = truncatedLabels.slice(0, maxValues - 1);
      truncatedLabels.push(othersLabel);
    }

    return truncatedLabels.map((label: string, index: number) => {
      const rate = truncatedData[index].rate;
      return rate !== undefined
        ? `${this.getFormattedText(
            label
          )} (${this.formatUtilitiesService.formatWithThousandsSeparators(
            rate
          )}%)`
        : this.getFormattedText(label);
    });
  }

  public groupDataAfterNthValue(
    data: DoughnutData[],
    maxValues: number
  ): DoughnutData[] {
    if (maxValues < 1) {
      return data;
    }
    let truncatedData = data.slice(0);
    if (data.length > maxValues) {
      truncatedData = truncatedData.slice(0, maxValues);
      truncatedData[maxValues - 1] = data.slice(maxValues).reduce(
        (result, current) => {
          if (result.rate && current.rate) {
            result.rate += current.rate;
          }
          result.value += current.value;
          return result;
        },
        { ...data[maxValues - 1] }
      );
    }
    return truncatedData;
  }
}

import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class FormatUtilitiesService {
  /**
   * Format ticks with optional unit.
   * @param val The value to format
   * @param unit Optional unit to append to the value
   * @returns Formatted string with thousands separators and unit (if provided)
   */
  public formatTicks(val: number, unit?: string): string {
    const fixedValue = parseInt(val.toFixed());
    return `${new Intl.NumberFormat().format(fixedValue)}${
      unit ? " " + unit : ""
    }`;
  }

  /**
   * Format value with thousands separators.
   * @param value The value to format
   * @returns Formatted value with K for thousands and M for millions
   */
  public formatWithThousandsSeparators(value: number): string {
    if (Math.abs(Number(value)) >= 1.0e6) {
      return this.formatDecimalNumber(value / 1.0e6) + " M";
    } else if (Math.abs(Number(value)) >= 1.0e3) {
      return this.formatDecimalNumber(value / 1.0e3) + " K";
    } else {
      return this.formatDecimalNumber(value);
    }
  }

  /**
   * Format a number with thousand separators.
   * @param value The value to format
   * @param unit Optional unit to append to the value
   * @returns Formatted number with thousand separators
   */
  public numberWithThousandSeparators(value: string, unit?: string): string {
    const x = parseFloat(value).toFixed(2);
    const formattedValue = x.replace(/\B(?=(\d{3}){1,5}(?!\d))/g, " ");
    return unit ? formattedValue + " " + unit : formattedValue;
  }

  /**
   * Get the shifted pattern index based on the dataset index.
   * @param dataSetIndex The index of the dataset
   * @param patternShifting Optional pattern shift value
   * @returns The shifted pattern index
   */
  public getPatternIndexWithShift(
    dataSetIndex: number,
    patternShifting?: number
  ): number {
    return patternShifting
      ? (dataSetIndex + patternShifting) % 6
      : dataSetIndex;
  }

  /**
   * Format value and rate for doughnut chart data.
   * @param doughnutData The doughnut chart data object
   * @param dataIndex The index of the data point
   * @returns Formatted string with value and rate percentage
   */
  public formatValueAndRate(doughnutData: any, dataIndex: number): string {
    const textValue = `${this.formatWithThousandsSeparators(
      doughnutData.data[dataIndex].value
    )} (${this.formatWithThousandsSeparators(
      doughnutData.data[dataIndex].rate
    )}%)`;
    return textValue;
  }

  /**
   * Format a decimal number with two decimal places.
   * @param value The number to format
   * @returns Formatted decimal number
   */
  private formatDecimalNumber(value: number): string {
    return new Intl.NumberFormat(
      new Intl.NumberFormat().resolvedOptions().locale,
      {
        style: "decimal",
        minimumFractionDigits: 2,
        maximumFractionDigits: 2,
      }
    ).format(value);
  }
}

import { Injectable } from "@angular/core";
import { TooltipChartType } from "../types/tooltip-chart-type";
import { PatternService } from "./pattern.service";

type BodyItem = {
  after: string[];
  before: string[];
  lines: string[];
};

export type TooltipElements = {
  chartType: TooltipChartType;
  firstLineLabel?: string;
  secondLineLabel?: string;
  patternShifting?: number;
};

type LabelColors = {
  backgroundColor: unknown;
  borderColor: unknown;
};

type PointStyle = {
  pointStyle: unknown;
  rotation: number;
};

export type Context = {
  chart: {
    canvas: HTMLElement;
    tooltip?: {
      x: number;
      y: number;
    };
  };
  replay?: unknown;
  tooltip: {
    dataPoints: {
      dataset?: {
        type?: string;
      };
      dataIndex?: number;
      datasetIndex?: number;
      raw: any;
    }[];
    opacity: number;
    body: {
      after: string[];
      before: string[];
      lines: string[];
    }[];
    title: string[];
    labelColors: Array<LabelColors>;
    labelPointStyles: Array<PointStyle>;
    caretX: number;
    caretY: number;
  };
};

@Injectable({
  providedIn: "root",
})
export class GenericTooltipService {
  chartType = "";
  datasetIndex = 0;
  dataIndex = 0;
  titleLines = [""];
  dataToDisplay = "";
  xValue = "";
  yValue = "";
  patternShifting = 0;

  constructor(private readonly patternService: PatternService) {}

  createTooltip(
    context: Context,
    retrieveData: (context: Context) => string,
    tooltipInputElements: TooltipElements,
    patternsColors: string[],
    patternsList: ((
      hover: boolean,
      color: string,
      disableAccessibility: boolean
    ) => CanvasPattern)[],
    disableAccessibility: boolean = false
  ) {
    if (!context.tooltip.dataPoints) {
      return;
    }
    this.datasetIndex = context.tooltip?.dataPoints[0].datasetIndex ?? 0;
    this.dataIndex = context.tooltip.dataPoints[0].dataIndex ?? 0;
    this.xValue = tooltipInputElements.firstLineLabel ?? "";
    this.yValue = tooltipInputElements.secondLineLabel ?? "";
    this.chartType = tooltipInputElements.chartType;
    this.dataToDisplay = retrieveData(context);
    this.patternShifting = tooltipInputElements.patternShifting ?? 0;
    let tooltipEl: HTMLElement | null =
      document.querySelector("#chartjs-tooltip");

    if (!tooltipEl) {
      tooltipEl = this.createNewTooltipElement();
    }

    const tooltipModel = context.tooltip;

    if (tooltipModel.opacity === 0) {
      tooltipEl.style.opacity = "0";
      return;
    }

    if (tooltipModel.body) {
      this.titleLines = tooltipModel.title || [];
      const bodyLines = tooltipModel.body.map(this.getBody);
      let style = "background: white;";
      style += "border-bottom: 1px solid #CCCCCC;";
      style += "border-radius: 5px;";
      style += "padding: 10px 20px";

      const innerHtml = `<div style="${style}" class="tooltipTitle">`;
      const body =
        this.chartType === "DOUGHNUT"
          ? [tooltipModel.title[0].split("(")[0].trim()]
          : bodyLines[0];
      let legendIconStyle = "";
      let legendInnerStyle = "";
      const datasetType = context.tooltip?.dataPoints[0]?.dataset?.type;

      if (this.chartType === "RADAR" || this.chartType === "LINE_CHART") {
        legendIconStyle = this.createLegendStyle(context);
        legendInnerStyle = this.createLegendInnerStyle(context);
      } else if (
        this.chartType === "BAR_CHART" ||
        this.chartType === "DETAILS_BAR_CHART" ||
        this.chartType === "DOUGHNUT"
      ) {
        legendIconStyle = this.createPatternLegendStyle(context);
      } else if (this.chartType === "MIXED_BAR_LINE_CHART") {
        if (datasetType === "bar") {
          legendIconStyle = this.createPatternLegendStyle(context);
        } else {
          legendIconStyle = this.createLegendStyle(context);
          legendInnerStyle = this.createLegendInnerStyle(context);
        }
      }

      this.addLegendToDom(
        innerHtml,
        legendIconStyle,
        legendInnerStyle,
        body,
        style,
        tooltipEl,
        patternsColors,
        patternsList,
        disableAccessibility,
        datasetType
      );
    }

    this.handleTooltipPosition(context, tooltipModel, tooltipEl);
  }

  protected handleTooltipPosition(
    context: Context,
    tooltipModel: { caretX: number; caretY: number },
    tooltipEl: HTMLElement
  ) {
    const position = context.chart.canvas.getBoundingClientRect();
    const screenWidth = window.innerWidth;
    const left = position.left + window.scrollX + tooltipModel.caretX;
    const top = position.top + window.scrollY + tooltipModel.caretY;

    tooltipEl.style.left = left + "px";
    tooltipEl.style.top = top + "px";
    tooltipEl.style.height = "auto";
    tooltipEl.style.minWidth = "17rem";
    tooltipEl.style.opacity = "1";
    tooltipEl.style.position = "absolute";
    tooltipEl.style.zIndex = "99";
    tooltipEl.style.backgroundColor = "white";
    tooltipEl.style.pointerEvents = "none";

    if (tooltipEl.getBoundingClientRect().width + left > screenWidth) {
      tooltipEl.style.left =
        left - tooltipEl.getBoundingClientRect().width + "px";
    }
  }

  protected createNewTooltipElement() {
    const tooltipEl = document.createElement("div");
    tooltipEl.id = "chartjs-tooltip";
    tooltipEl.style.backgroundColor = "white";
    tooltipEl.style.borderRadius = "5px";
    tooltipEl.style.transition = "opacity .5s";
    tooltipEl.style.boxShadow = "0px 1px 5px rgba(0, 0, 0, 0.2)";
    tooltipEl.innerHTML = '<div class="tooltipCtn"></div>';
    document.body.appendChild(tooltipEl);
    return tooltipEl;
  }

  private createPatternLegendStyle(context: Context) {
    return this.createCommonLegendSquareStyle(context);
  }

  private createLegendStyle(context: Context) {
    let legendIconStyle = `background-color:${context.tooltip.labelColors[0].backgroundColor}`;
    legendIconStyle += this.createCommonLegendSquareStyle(context);
    return legendIconStyle;
  }

  private createCommonLegendSquareStyle(context: Context): string {
    let style = `;border: 2px solid ${context.tooltip.labelColors[0].borderColor}`;
    style += ";min-height: 20px";
    style += ";min-width: 20px";
    style += ";border-radius: 5px";
    style += ";margin-right: 10px";
    style += ";display: flex";
    style += ";align-items: center";
    style += ";justify-content: center";
    style += ";background: rgba(0, 0, 0, 0.1);";
    return style;
  }

  createLegendInnerStyle(context: Context): string {
    let legendIconInnerStyle = "height: 12px";
    legendIconInnerStyle += ";width: 12px";
    legendIconInnerStyle += ";background-color: #FFF";
    legendIconInnerStyle += `;border: 2px solid ${context.tooltip.labelColors[0].borderColor};`;

    if (context.tooltip.labelPointStyles[0].pointStyle === "circle") {
      legendIconInnerStyle += "border-radius: 25px;";
    } else if (context.tooltip.labelPointStyles[0].pointStyle === "rectRot") {
      legendIconInnerStyle += "transform: rotate(45deg);";
    }
    return legendIconInnerStyle;
  }

  private addLegendToDom(
    innerHTMLtext: string,
    legendIconStyle: string,
    legendIconInnerStyle: string,
    body: Array<string>,
    style: string,
    tooltipEl: HTMLElement,
    patternsColors: string[],
    patternsList: ((
      hover: boolean,
      color: string,
      disableAccessibility: boolean
    ) => CanvasPattern)[],
    disableAccessibility: boolean = false,
    datasetType?: string
  ) {
    let innerHtml = innerHTMLtext;
    let legendImage = `<div class="legendIcon" style="${legendIconStyle}">`;
    const legendSubImage = `<div style="${legendIconInnerStyle}"></div>`;
    legendImage += `${legendSubImage}</div>`;

    const innerHtmlToAdd = this.setInnerHtmlToAdd(body, style, legendImage);
    innerHtml += innerHtmlToAdd;
    const tableRoot: HTMLElement | null =
      tooltipEl?.querySelector(".tooltipCtn");
    if (tableRoot?.innerHTML != null) {
      datasetType
        ? this.setInnerHtmlAndPattern(
            tableRoot,
            innerHtml,
            patternsColors,
            patternsList,
            disableAccessibility,
            datasetType
          )
        : this.setInnerHtmlAndPattern(
            tableRoot,
            innerHtml,
            patternsColors,
            patternsList,
            disableAccessibility
          );
    }
  }

  setInnerHtmlToAdd(body: Array<string>, style: string, legendImage: string) {
    const legendText = body[0].split(":")[0];
    const fontProperties = "font-family: Arial; font-size: 16px";
    const spanText = `<span style="${fontProperties}">${legendText}</span>`;
    if (this.chartType === "RADAR") {
      return this.returnRadarHtml(style, legendImage, spanText);
    } else if (this.chartType === "DOUGHNUT") {
      return this.returnDoughnutHtml(legendImage, spanText);
    } else {
      return this.returnDetailsBarchartHtml(style, legendImage, spanText);
    }
  }

  returnDoughnutHtml(legendImage: string, spanText: string) {
    const fontProperties = "font-family: Arial; font-size: 16px";
    const legendText = `<span style="${fontProperties}">${
      spanText.split("(")[0]
    }</span>`;
    let doughnutHtml = `<div style="${fontProperties}; display: flex; align-items: center; justify-content: space-between">`;
    doughnutHtml += `<div style="display:flex; align-items: center;" >${
      legendImage + legendText
    }</div>`;
    doughnutHtml += `<div style="${fontProperties}; margin-left:3rem;">${this.dataToDisplay}</div>`;
    doughnutHtml += "</div></div>";
    return doughnutHtml;
  }

  returnRadarHtml(style: string, legendImage: string, spanText: string) {
    const fontProperties = "font-family: Arial; font-size: 16px";
    let radarHtml = `<div style="${fontProperties}; display: flex; align-items: center;">${
      legendImage + spanText
    }</div>`;
    radarHtml += "</div>";
    radarHtml += `<div style="${fontProperties}; ${style}; border: none; display:flex; justify-content: space-between;">`;
    radarHtml += `<div>${this.titleLines[0]}</div>`;
    radarHtml += `<div style="margin-left: 20px;">${this.dataToDisplay}</div>`;
    radarHtml += "</div>";
    radarHtml += "</div><div>";

    return radarHtml;
  }

  returnDetailsBarchartHtml(
    style: string,
    legendImage: string,
    spanText: string
  ) {
    const fontProperties = "font-family: Arial; font-size: 16px";
    let barChartHtml = `<div style="${fontProperties}; display: flex; align-items: center;">${
      legendImage + spanText
    }</div>`;
    barChartHtml += "</div>";
    barChartHtml += `<div style="${fontProperties}; ${style}; display:flex; justify-content: space-between;">`;
    barChartHtml += `<div>${this.xValue}</div>`;
    barChartHtml += `<div style="margin-left: 20px;">${this.titleLines[0]}</div>`;
    barChartHtml += "</div>";

    barChartHtml += `<div style="${fontProperties}; ${style}; border-: none; display:flex; justify-content: space-between;">`;
    barChartHtml += `<div>${this.yValue}</div>`;
    barChartHtml += `<div style="margin-left: 20px;">${this.dataToDisplay}</div>`;
    barChartHtml += "</div>";

    return barChartHtml;
  }

  setInnerHtmlAndPattern(
    tableRoot: HTMLElement,
    innerHtml: string,
    patternsColors: string[],
    patternsList: ((
      hover: boolean,
      color: string,
      disableAccessibility: boolean
    ) => CanvasPattern)[],
    disableAccessibility: boolean = false,
    datasetType?: string
  ) {
    tableRoot.innerHTML = innerHtml;
    const legendIconHtml = document.querySelector(".legendIcon") as HTMLElement;
    const img: HTMLImageElement = new Image();

    let index: number;

    if (this.chartType === "DOUGHNUT") {
      index = this.dataIndex + 1;
    } else {
      index = this.datasetIndex + 1;
    }
    const patternIndex = this.patternService.getPatternIndexWithShift(
      index,
      this.patternShifting
    );
    if (
      this.chartType !== "LINE_CHART" &&
      this.chartType !== "RADAR" &&
      datasetType !== "line"
    ) {
      const pattern: CanvasPattern = patternsList[patternIndex - 1](
        false,
        patternsColors[patternIndex - 1],
        disableAccessibility
      );
      const patternCanvas: HTMLCanvasElement =
        this.patternService.getPatternCanvas(pattern, 22, 22);
      img.src = patternCanvas.toDataURL();
      legendIconHtml.style.backgroundImage = `url(${img.src})`;
    }
  }

  getBody(bodyItem: BodyItem) {
    return bodyItem.lines;
  }
}

import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class PatternService {
  constructor() {}

  getPatternCanvas(
    pattern: CanvasPattern,
    width = 50,
    height = 50
  ): HTMLCanvasElement {
    const canvas: HTMLCanvasElement = document.createElement("canvas");
    const ctx: CanvasRenderingContext2D | null = canvas.getContext("2d");
    if (!ctx) {
      return canvas;
    }
    canvas.width = width;
    canvas.height = height;
    ctx.fillStyle = pattern;
    ctx.fillRect(0, 0, width, height);
    return canvas;
  }

  getPatternIndexWithShift(
    dataSetIndex: number,
    patternShifting?: number
  ): number {
    return (
      (patternShifting ? dataSetIndex + patternShifting : dataSetIndex) % 6
    );
  }
}

